create table beer (
  id INT UNSIGNED PRIMARY KEY AUTO_INCREMENT NOT NULL,
  type VARCHAR(50) NOT NULL,
  name VARCHAR(100) NOT NULL,
  tagline VARCHAR(255) NOT NULL,
  first_brewed VARCHAR(20) NOT NULL,
  description VARCHAR(755) NOT NULL,
  image_url VARCHAR(255) NOT NULL,
  abv FLOAT NOT NULL,
  ibu FLOAT NULL,
  price_per_liter FLOAT NOT NULL,
  volume_value INTEGER NOT NULL,
  volume_unit VARCHAR(6) NOT NULL,
  boil_volume_value INTEGER NOT NULL,
  boil_volume_unit VARCHAR(6) NOT NULL,
  method_fermentation_temp_value INTEGER NOT NULL,
  method_fermentation_temp_unit VARCHAR(7) NOT NULL,
  brewers_tips VARCHAR(257) NOT NULL,
  contributed_by VARCHAR(255) NOT NULL
);
